content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  const alamat = await db.alamat.findMany({
    where: {
      id_customer: Number(params.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data alamat",
    data: alamat,
  })
}
