content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  try {
    const order = await db.transaksi.update({
      where: {
        id: Number(params.id),
      },
      data: {
        status_transaksi: 1,
      },
    });

    if (order) {
      const notif = await db.notif.create({
        data: {
          title: "Order Cancelled",
          desc: "Orders has been cancelled",
          id_customer: order.id_customer,
          created_at: new Date(ext.nowLocal()),
        },
      });

      if (notif) {
        ext.socket.emit("admin", {
          event: "notif",
          user_id: notif.id_customer,
          data: {
            title: notif.title,
            desc: notif.desc,
            created_at: notif.created_at,
          },
        });
      } else {
        console.log("Notif tidak tersimpan");
      }
    }

    reply.send({
      status: "SUCCESS",
      message: "Berhasil cancel order",
      data: order,
    });
  } catch (e) {
    reply.status(500).send({
      status: "ERROR",
      message: e.message,
    });
  }
}
