content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  try {
    const order = await db.transaksi.update({
      where: {
        id: Number(params.id),
      },
      data: {
        tanggal_pemesanan: null,
        status_transaksi: null,
        tanggal_peminjaman: null,
        tanggal_pengembalian: null,
        total_harga: null,
      },
    });

    reply.send({
      status: "SUCCESS",
      message: "Berhasil cancel order",
      data: order,
    });
  } catch (e) {
    reply.status(500).send({
      status: "ERROR",
      message: e.message,
    });
  }
}
