content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const detail = await db.detail_transaksi.findMany({
    where: {
      id_transaksi: Number(params.id),
    },
    include: {
      transaksi: true,
    },
  });

  if (detail.length <= 0) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Transaksi tidak ditemukan",
    });
  }

  try {
    const order = db.transaksi.update({
      where: {
        id: Number(params.id),
      },
      data: {
        status_transaksi: 1,
      },
    });

    // Untuk mengembalikan lagi stok barangnya
    detail.forEach(async (item) => {
      await await db.$queryRaw(`
            UPDATE barang
            SET stok_barang = stok_barang + ${item.quantity}
            WHERE id = ${item.id_barang}
        `);
    });

    const notif = db.notif.create({
      data: {
        title: "Order Cancelled",
        desc: "Orders has been cancelled",
        id_customer: detail[0].transaksi.id_customer,
        created_at: new Date(ext.nowLocal()),
      },
    });

    const run = await db.$transaction([order, notif]);

    if (notif) {
      ext.socket.emit("admin", {
        event: "notif",
        user_id: run[1].id_customer,
        data: {
          message: run[1].id_customer,
          desc: run[1].desc,
          created_at: run[1].created_at,
        },
      });
    } else {
      console.log("Notif tidak tersimpan");
    }

    reply.send({
      status: "SUCCESS",
      message: "Berhasil cancel order",
      data: run[0],
    });
  } catch (e) {
    reply.status(500).send({
      status: "ERROR",
      message: e.message,
    });
  }
}
