content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { username, email, nama, no_hp } = req.body;

  const admin = await db.admin.update({
    where: {
      id: Number(params.id),
    },
    data: {
      username: username,
      email: email,
      nama: nama,
      no_hp: no_hp,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data admin",
    data: admin,
  })
}
