content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role != "super") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya super admin yang bisa mengakses ini",
    });
  }

  const { username, email, nama, no_hp, role } = req.body;
  // const is_role = role || role == "true" ? true : false;
  if (role == "true" || role == true) {
    var is_role = true;
  }
  if (role == "false" || role == false) {
    var is_role = false;
  }

  const admin = await db.admin.update({
    where: {
      id: Number(params.id),
    },
    data: {
      username: username,
      email: email,
      nama: nama,
      no_hp: no_hp,
      role: is_role,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data admin",
    data: admin,
  })
}
