content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  user.user = {
    role: "guest",
  };

  reply.send({
    status: "SUCCESS",
    message: "Berhasil logout",
  })
}
