content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const password = await ext.argon2.hash(req.body.password);

  if (user.user.role == "admin") {
    await db.admin.update({
      where: {
        id: user.user.admin.id,
      },
      data: {
        password: password,
      },
    });
  }

  if (user.user.role == "customer") {
    await db.customer.update({
      where: {
        id: user.user.customer.id,
      },
      data: {
        password: password,
      },
    });
  }

  reply.send({
    status: "SUCCESS",
    message: "Berhasil merubah password",
  })
}
