content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const page = req.query.page ? req.query.page : 1;
  const perPage = req.query.perPage ? req.query.perPage : 10;
  const search = req.query.search ? req.query.search : "";

  const customer = await db.customer.findMany({
    skip: Number((page - 1) * perPage),
    take: Number(perPage),
    where: {
      OR: [
        {
          email: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          nama_customer: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          nama_perusahaan: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          no_hp: {
            contains: search,
            mode: "insensitive",
          },
        },
      ],
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data customer",
    data: customer,
  })
}
