content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const {
    kategori_alamat,
    nama,
    no_hp,
    provinsi,
    kota,
    kecamatan,
    alamat_pengiriman,
    kode_pos,
    informasi_tambahan,
  } = req.body;
  console.log(params.id);
  const alamat = await db.alamat.update({
    where: {
      id: Number(params.id),
    },
    data: {
      kategori_alamat,
      nama,
      no_hp,
      provinsi,
      kecamatan,
      kota,
      alamat_pengiriman,
      kode_pos,
      informasi_tambahan,
    },
  });

  reply.status(203).send({
    status: "SUCCESS",
    message: "Berhasil mengubah data alamat",
    data: alamat,
  })
}
