content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cart = await db.detail_transaksi.findMany({
    where: {
      transaksi: {
        AND: [
          {
            id_customer: Number(params.id),
          },
          {
            tanggal_pemesanan: null,
          },
        ],
      },
    },
    select: {
      id: true,
      barang: {
        select: {
          id: true,
          nama_barang: true,
          harga_barang: true,
          stok_barang: true,
          gambar_barang: true,
        },
      },
      quantity: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data barang dalam cart",
    data: cart,
  })
}
