content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cart = await db.cart.findMany({
    where: {
      id_customer: Number(params.id),
    },
    select: {
      id: true,
      barang: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data admin",
    data: cart,
  })
}
