content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const page = req.query.page ? req.query.page : 0;
  const perPage = req.query.perPage ? req.query.perPage : 10;
  const search = req.query.search ? req.query.search : "";
  const category = req.query.category;

  let sortby = req.query.sortby ? req.query.sortby : "id asc";
  sortby = sortby.split(" ");

  if (category) {
    const barang = await db.$queryRaw(`
      SELECT *
      FROM barang WHERE 
      (nama_barang LIKE '%${search}%'
      OR merk_barang LIKE '%${search}%'
      OR detail_barang LIKE '%${search}%')
      AND kategori_barang IN (${category})
      ORDER BY ${sortby[0]} ${sortby[1]}
      LIMIT ${perPage}
    `);

    reply.send({
      status: "SUCCESS",
      message: "Berhasil get data barang",
      data: barang,
    });
  } else {
    const barang = await db.$queryRaw(`
      SELECT *
      FROM barang WHERE 
      (nama_barang LIKE '%${search}%'
      OR merk_barang LIKE '%${search}%'
      OR detail_barang LIKE '%${search}%')
      ORDER BY ${sortby[0]} ${sortby[1]}
      LIMIT ${perPage}
    `);

    reply.send({
      status: "SUCCESS",
      message: "Berhasil get data barang",
      data: barang,
    });
  }
}
