content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const page = req.query.page ? req.query.page : 0;
  const perPage = req.query.perPage ? req.query.perPage : 10;
  const search = req.query.search ? req.query.search : "";

  const barang = await db.barang.findMany({
    skip: Number(page),
    take: Number(perPage),
    where: {
      OR: [
        {
          nama_barang: {
            contains: search,
          },
        },
        {
          jenis_barang: {
            contains: search,
          },
        },
        {
          merk_barang: {
            contains: search,
          },
        },
        {
          detail_barang: {
            contains: search,
          },
        },
      ],
    },
    select: {
      id: true,
      nama_barang: true,
      merk_barang: true,
      harga_barang: true,
      gambar_barang: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data barang",
    data: barang,
  })
}
