content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const barang = await db.barang.findMany({
    select: {
      id: true,
      nama_barang: true,
      merk_barang: true,
      harga_barang: true,
      gambar_barang: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data barang",
    data: barang,
  })
}
