content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const page = req.query.page ? req.query.page : 0;
  const perPage = req.query.perPage ? req.query.perPage : 10;

  const barang = await db.barang.findMany({
    skip: Number(page),
    take: Number(perPage),
    select: {
      id: true,
      nama_barang: true,
      merk_barang: true,
      harga_barang: true,
      gambar_barang: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data barang",
    data: barang,
  })
}
